CREATE TABLE "public"."users" ("id" serial NOT NULL, "google_id" text NOT NULL, "email" text NOT NULL, "display_name" text, "photo_url" text, "last_login" timestamptz, "is_admin" boolean DEFAULT false, "telegram_id" text, "callback_url" text, "webhook_secret" text, "status" integer DEFAULT 1, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , UNIQUE ("google_id"), UNIQUE ("email"));
